import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from "react";
import axios from 'axios';

function App() {
    const [students, setStudents] = useState([]);
    const [formData, setFormData] = useState({
        name: '',
        age: '',
        grade: '',
        phoneNumber: '',
        parentName: '',
        parentNumber: '',
        gender: '',
        level: ''
    });
    const [isEditing, setIsEditing] = useState(false);
    const [editingId, setEditingId] = useState(null);

    useEffect(() => {
        fetchStudents();
    }, []);

    const fetchStudents = async () => {
        try {
            const response = await axios.get('/api/students');
            setStudents(response.data);
        } catch (error) {
            console.error('Error fetching student data:', error);
        }
    };

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async () => {
        try {
            if (isEditing) {
                await axios.put(`/api/students/${editingId}`, formData);
                setIsEditing(false);
                setEditingId(null);
            } else {
                await axios.post('/api/students', formData);
            }
            setFormData({
                name: '',
                age: '',
                grade: '',
                phoneNumber: '',
                parentName: '',
                parentNumber: '',
                gender: '',
                level: ''
            });
            fetchStudents(); // 학생 정보를 다시 가져와서 업데이트
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const handleEdit = (student) => {
        setIsEditing(true);
        setEditingId(student.id);
        setFormData({
            name: student.name,
            age: student.age,
            grade: student.grade,
            phoneNumber: student.phoneNumber,
            parentName: student.parentName,
            parentNumber: student.parentNumber,
            gender: student.gender,
            level: student.level
        });
    };

    return (
        <div>
            <h1>학생 추가하기</h1>
            {/* 입력 필드 */}
            <button onClick={handleSubmit}>{isEditing ? '수정' : '추가'}</button>
            <h1>학생 리스트</h1>
            {/* 학생 목록 */}
            {students.map((student, index) => (
                <div key={index}>
                    id: {student.id}, 이름: {student.name}, 나이: {student.age},
                    <button onClick={() => handleEdit(student)}>수정</button>
                </div>
            ))}
        </div>
    );
}

export default App;
