import logo from './logo.svg';
import './App.css';
import {useEffect, useState} from "react";
import  axios from 'axios';

function App() {
  const [students, setStudent] = useState([])

  useEffect(() => {
    axios.get('/api/students')
        .then(response => {
          setStudent(response.data);
        })
        .catch(error => {
          console.error('Error fetching student data:', error);
        })
  }, []);

    const [formData, setFormData] = useState({
        name: '',
        age: '',
        grade: '',
        phoneNumber: '',
        parentName: '',
        parentNumber: '',
        gender: '',
        level: ''
    });

    const [isEditing, setIsEditing] = useState(false);
    const [editingId, setEditingId] = useState(null);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async () => {
        try {
            const response = await axios.post('/api/students', formData);
            console.log('Response:', response.data);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.post(`/api/delete/students/${id}`);
            const updatedStudents = students.filter(student => student.id !== id);
            setStudent(updatedStudents); // 삭제된 학생을 제외한 목록으로 업데이트
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const handleEdit = (student) => {
        setIsEditing(true);
        setEditingId(student.id);
        setFormData({
            name: student.name,
            age: student.age,
            grade: student.grade,
            phoneNumber: student.phoneNumber,
            parentName: student.parentName,
            parentNumber: student.parentNumber,
            gender: student.gender,
            level: student.level
        });
    };

  return (
      <div>
          <h1>학생 추가하기</h1>
          <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="이름"
          />
          <input
              type="number"
              name="age"
              value={formData.age}
              onChange={handleChange}
              placeholder="나이"
          />
          <input
              type="number"
              name="grade"
              value={formData.grade}
              onChange={handleChange}
              placeholder="학년"
          />
          <input
              type="text"
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleChange}
              placeholder="전화번호"
          />
          <input
              type="text"
              name="parentName"
              value={formData.parentName}
              onChange={handleChange}
              placeholder="부모님 성함"
          />
          <input
              type="text"
              name="parentNumber"
              value={formData.parentNumber}
              onChange={handleChange}
              placeholder="부모님 전화번호"
          />
          <input
              type="text"
              name="gender"
              value={formData.gender}
              onChange={handleChange}
              placeholder="성별"
          />
          <input
              type="text"
              name="level"
              value={formData.level}
              onChange={handleChange}
              placeholder="레벨"
          />
          <button onClick={handleSubmit}>추가</button>
          <h1>학생리스트</h1>
          {students.map((student, index) => (
              <div key={index}>
                  id: {student.id} 이름: {student.name}, 나이: {student.age},<button onClick={() => handleDelete(student.id)}>삭제</button>
                  <button>수정</button>
              </div>
          ))}
      </div>

  )
}

export default App;
