import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from "react";
import axios from 'axios';

function App() {
    const [students, setStudents] = useState([]);
    const [formData, setFormData] = useState({
        name: '',
        age: '',
        grade: '',
        phoneNumber: '',
        parentName: '',
        parentNumber: '',
        gender: '',
        level: ''
    });
    const [editIndex, setEditIndex] = useState(null); // 수정 중인 학생의 인덱스를 저장

    useEffect(() => {
        axios.get('/api/students')
            .then(response => {
                setStudents(response.data);
            })
            .catch(error => {
                console.error('Error fetching student data:', error);
            })
    }, []);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async () => {
        try {
            const response = await axios.post('/api/students', formData);
            console.log('Response:', response.data);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.post(`/api/delete/students/${id}`);
            const updatedStudents = students.filter(student => student.id !== id);
            setStudents(updatedStudents); // 삭제된 학생을 제외한 목록으로 업데이트
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const handleEditToggle = (index) => {
        setEditIndex(index); // 수정 중인 학생의 인덱스 설정
    };

    const handleEditCancel = () => {
        setEditIndex(null); // 수정 취소 시 수정 중인 학생 인덱스 초기화
    };

    return (
        <div>
            <h1>학생 추가하기</h1>
            {/* 입력 폼들 */}
            <button onClick={handleSubmit}>추가</button>
            <h1>학생리스트</h1>
            {/* 학생 리스트 */}
            {students.map((student, index) => (
                <div key={index}>
                    {/* 학생 정보 */}
                    id: {student.id} 이름: {student.name}, 나이: {student.age},
                    {/* 삭제 버튼 */}
                    <button onClick={() => handleDelete(student.id)}>삭제</button>
                    {/* 수정 버튼 */}
                    <button onClick={() => editIndex === index ? handleEditCancel() : handleEditToggle(index)}>
                        {editIndex === index ? '취소' : '수정'}
                    </button>
                    {/* 수정 폼 */}
                    {editIndex === index && (
                        <div>
                            <h1>수정하기</h1>
                            {/* 수정 폼 입력 */}
                            <button>수정완료</button>
                        </div>
                    )}
                </div>
            ))}
        </div>
    )
}

export default App;
